mapper.select:
select
  article_id, title, author, published_time, content, img, collection, share, comment, star, page_view
from a_article


        and article_id = #{articleId}


        and title = #{title}


        and author = #{author}


        and published_time = #{publishedTime}


        and content = #{content}


        and img = #{img}


        and collection = #{collection}


        and share = #{share}


        and comment = #{comment}


        and star = #{star}


        and page_view = #{pageView}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from a_article


        and article_id = #{articleId}


        and title = #{title}


        and author = #{author}


        and published_time = #{publishedTime}


        and content = #{content}


        and img = #{img}


        and collection = #{collection}


        and share = #{share}


        and comment = #{comment}


        and star = #{star}


        and page_view = #{pageView}
