mapper.select:
select
  id, appreciation, commentabled, content, create_time, description, first_picture, flag, published, recommend, share_statement, title, update_time, views, type_id, user_id
from t_blog


        and id = #{id}


        and appreciation = #{appreciation}


        and commentabled = #{commentabled}


        and content = #{content}


        and create_time = #{createTime}


        and description = #{description}


        and first_picture = #{firstPicture}


        and flag = #{flag}


        and published = #{published}


        and recommend = #{recommend}


        and share_statement = #{shareStatement}


        and title = #{title}


        and update_time = #{updateTime}


        and views = #{views}


        and type_id = #{typeId}


        and user_id = #{userId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_blog


        and id = #{id}


        and appreciation = #{appreciation}


        and commentabled = #{commentabled}


        and content = #{content}


        and create_time = #{createTime}


        and description = #{description}


        and first_picture = #{firstPicture}


        and flag = #{flag}


        and published = #{published}


        and recommend = #{recommend}


        and share_statement = #{shareStatement}


        and title = #{title}


        and update_time = #{updateTime}


        and views = #{views}


        and type_id = #{typeId}


        and user_id = #{userId}
